<template>    <div class="layout">        <Layout>            <Header>                <Menu @on-select="gotoOther" mode="horizontal" theme="dark" active-name="3">                    <div class="title" @click="gotoIndexPage">华云学生成绩管理系统</div>                    <div class="layout-nav">                        <MenuItem name="1" data-url="checkStudent">                            <Icon type="ios-navigate"></Icon>                            学生管理                        </MenuItem>                        <MenuItem name="2" v-if="power == 1" data-url="teacher">                            <Icon type="ios-navigate"></Icon>                            教师管理                        </MenuItem>                        <MenuItem name="3">                            <Dropdown>                                <a href="javascript:void(0)" class="menu">                                    更多                                    <Icon type="arrow-down-b"></Icon>                                </a>                                <DropdownMenu slot="list">                                    <DropdownItem>退出</DropdownItem>                                </DropdownMenu>                            </Dropdown>                        </MenuItem>                    </div>                </Menu>            </Header>            <Content :style="{padding: '20px 50px 0'}">                <!-- <Breadcrumb :style="{margin: '20px 0'}">                    <BreadcrumbItem>Home</BreadcrumbItem>                    <BreadcrumbItem>Components</BreadcrumbItem>                    <BreadcrumbItem>Layout</BreadcrumbItem>                </Breadcrumb> -->                <Card>                    <div style="min-height: 500px;">                        <router-view/>                    </div>                </Card>            </Content>            <Footer class="layout-footer-center">2017-2018 &copy; 华云学生成绩管理系统</Footer>        </Layout>    </div></template><script>    export default {        data() {            return {                power: 2,                menuUrl: [                    '',                    'student',                    'teacher',                ],            }        },        mounted() {            this.power = sessionStorage.user_power;        },        methods: {            gotoOther(e) {                const { menuUrl } = this;                this.$router.push({ name: menuUrl[e] });            },            gotoIndexPage() {                this.$router.push({ name: 'welcome' });            },        },    }</script><style>.title{    float: left;    line-height: 64px;    color: #fff;    font-size: 18px;    cursor: pointer;}.layout{    border: 1px solid #d7dde4;    background: #f5f7f9;    position: relative;    border-radius: 4px;    overflow: hidden;}.layout-logo{    width: 100px;    height: 30px;    background: #5b6270;    border-radius: 3px;    float: left;    position: relative;    top: 15px;    left: 20px;}.layout-nav{    float: right;    width: auto;    margin: 0 auto;    margin-right: 20px;}.layout-footer-center{    text-align: center;}.ivu-layout-content{    min-height: calc(100vh - 135px);    overflow: hidden;}.menu{    color: #eee;}</style>